var findSubstring = function(s, words) {
  
    var result =[];
    var permArr=[];
    if (words.length === 0){
        return result;
    }
    const permute = (arr, m = []) => {
        if (arr.length === 0) {
          //console.log(m.join(''));
          var concatStr = m.join('');
            
            for (var j=0;j<s.length;j++){
                //console.log('substr',s.substring(j,j+concatStr.length));
                //console.log('concat',concatStr)
                if (s.substring(j,j+concatStr.length) === concatStr){
                    if (!result.includes(j)){
                        result.push(j);
                    }
                }
            }
/*
          let indexPos = s.indexOf(concatStr);
            console.log(indexPos);
          if ( indexPos >=0 && !result.includes(indexPos)) {
            result.push(indexPos);  
          }
*/
          
        } else {
          for (let i = 0; i < arr.length; i++) {
                let prev = arr.slice();
                let curr = prev.splice(i, 1);
                permute(prev.slice(), m.concat(curr))
            }
        }
    }
    

    permute(words);
    console.log(result);
    return result;
};



